/* eslint no-console: 0 */
const { format } = require('./term');


class Logger {

    log(message = '') {
        console.log(message);
    }

    debug(message = '') {
        console.log(format.info(message));
    }

    warn(message = '') {
        console.warn(format.warn(message));
    }

    error(message = '') {
        console.error(format.error(message));
    }

}

module.exports = Logger;
