module.exports = {

    convertOptions(options) {
        const newOptions = {};
        for(let optionName in options) {
            if(optionName) {
                let option = options[optionName];
                let newOptionName = optionName.replace(/-([a-z])/g, (word) => word[1].toUpperCase());
                newOptions[newOptionName] = option;
            }
        }
        return newOptions;
    },

    generateCmdLine(configKey) {
        let cmdLineOption = configKey.replace(/([A-Z])/g, (word) => {
            return `-${word.toLowerCase()}`;
        });
        return `--${cmdLineOption}`;
    }
};
