/**
 * Cresate an error containing the actual error cause (target)
 */
class TargetError extends Error {

    constructor(message, error) {
        super(message);
        this.name = 'TargetError';
        this.targetError = error;
        this.message = `${message} ${error.message.split(/[\n\r]/g)[0]}`;
        this.stack = error.stack;
    }
}

module.exports = TargetError;
